import { Component } from "react";
const initialState = {
    age: 10,
    phone: 9800001123
};
class scoreBoard extends Component{
    constructor(props) {
        super(props);
        this.state = initialState;

        this.state = { ...this.state, popScore: props.scores  }
    }
    
    changeScore = () => {
        // this.props.popularity;
        this.setState(() => ({
            popScore: this.state.popScore + 1
        }));
    }
render(){
    return(
        <div style={{border:" 5px dotted blue"}}>
            <h1>My name is  {this.props.name}. My age is {this.state.age}.</h1>
            <scoreBoard name="Prakitha" scores={0}/>
                <h2>My current scores :{this.state.popScore}</h2>
        <button onClick={this.changeScore}>Click here to change score</button>
                </div>
    )
}
}
export default scoreBoard
