import { Component } from "react";

class ScoreBoard extends Component{
    constructor(props) {
        super(props);


        this.state = { ...this.state, popScore: props.score }
    }
    
    changeScore = () => {
        // this.props.popularity;
        this.setState(() => ({
            popScore: this.state.popScore + 1
        }));
    }
render(){
    return(
        <div style={{border:" 5px solid green"}}>
            <h1>player1:  {this.props.name1}.</h1>
            <h2>Player2:{this.props.name2}</h2>
                <h2>My current scores :{this.state.popScore}</h2>
        <button onClick={this.changeScore}>Click here to change score</button>
                </div>
    )
}
}
export default ScoreBoard
