import { Component } from "react";

class MyEventHandling extends Component {

    constructor(props){
        super(props);
        this.handleSubmit=this.handleSubmit.bind(this);
    }
    handleSubmit(e) {
        e.preventDefault();
        let age = document.forms[0].age;
        console.log('You clicked submit.');
        if (age > 60) {
            console.log('age is 60 so form submisson will happen ')
            document.forms[0].submit();
        }
    }

    render() {
        return (
            <div>
                <p>Event handling in react</p>>
                <form onSubmit={this.handleSubmit} action='process.php'>
                    <input type='text' name='age' />
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}
export default MyEventHandling;
