const ReducerConceptDemo=()=>{

    var counter=0;
    const increment=()=>{
        return counter+1;
    }
    let val1=increment();
    console.log(val1);
    
    val1=increment();
    console.log(val1);

    val1=increment();
    console.log(val1);

}
export default ReducerConceptDemo
