import { Component } from "react";

class Person extends Component {

    constructor(props) {
        super(props);

        this.state = {
            score1: props.score1,
            score2: props.score2
        }
    }

    f1 = () => {
        this.setState(
            {
                score1: this.state.score1 + Math.floor(Math.random() * 10),
                score2: this.state.score2 + Math.floor(Math.random() * 10)
                
            }
        );
    }
    f2=()=>{
        alert("in parent class");
    }

    render() {

        return (
            <p>
                Name: {this.props.name} Score1: {this.state.score1}
                
            </p>
        )

    }

}

export default Person;
