import React,{ Component } from "react";

class ScoreBoard extends Component{
    constructor(props) {
        super(props);
this.ref1=React.createRef();
this.ref2=React.createRef();

    }
    
    changeScore = () => {
        this.ref1.current.f1();
        this.ref2.current.f1();
    }
    
render(){
    return(
        <div style={{border:" 5px solid green"}}>
            <h1 style={{textAlign:"center"}}>Tennis</h1>
            <h3>player1:  {this.props.name1}.         score:{this.state.score}</h3>
            <h3>Player2:{this.props.name2}              score:{this.state.score}</h3>

                <h2>My total  scores :{this.state.popScore}</h2>
        <button onClick={this.changeScore}>Click here to change score</button>
                </div>
    )
}
}
export default ScoreBoard
