import { useState } from "react";

const PersonComp = (props) => {
    const [score, setScore] = useState(5);
    return (
        <div>Hello {props.name} and my score is {score} <br />
            <button onClick={() => setScore(score + 1)}>Click to see the score</button><br /></div>
    );
}
export default PersonComp
