import {useState} from "react";

const PersonComp=(props)=>{
const [score,setScore]=useState(5);
const ChangeScore=()=>{
    if(score%5==1)
    setScore(score+2)
else
setScore(setScore+1);
}
    return (
        <div>Hello {props.name} and my score is {score} <br/>
<button onClick={ChangeScore+1}>Click to see the score</button><br/></div>
    );
}
export default PersonComp
